import { useState } from "react";
import { Card, Form, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Signup() {
  const [error, setError] = useState("");
  const [user, setUser] = useState({
    email: "",
    fullName: "",
    password: "",
    image: "",
  });

  const [file, setFile] = useState();
  const [fileName, setFileName] = useState("");

  const saveFile = (e) => {
    setFile(e.target.files[0]);
    setFileName(e.target.files[0].name);
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    setUser((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("file", file);
    formData.append("fileName", fileName);
    debugger;
    try {
      const res = await axios.post("http://localhost:9000/upload", formData);
      console.log(res);
    } catch (ex) {
      console.log(ex);
    }

    setUser({
      email: "",
      fullName: "",
      password: "",
      confirmPassword: "",
    });
  };

  return (
    <>
      <Card className="my-5 container w-25 bg-light">
        <Card.Body>
          <h2 className="text-center mb-3">Sign Up</h2>
          <Form onSubmit={onSubmit}>
            <input onChange={saveFile} type="file" />
            <Form.Group id="email">
              <Form.Label>E-Mail</Form.Label>
              <Form.Control
                onChange={onChange}
                name="email"
                value={user?.email}
              />
            </Form.Group>

            <Form.Group id="name">
              <Form.Label>Full Name</Form.Label>
              <Form.Control
                onChange={onChange}
                name="fullName"
                value={user?.fullName}
              />
            </Form.Group>
            <Form.Group id="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                onChange={onChange}
                name="password"
                value={user?.password}
              />
            </Form.Group>
            <Form.Group id="password-confirm">
              <Form.Label>Confirm Password</Form.Label>
              <Form.Control type="password" required />
            </Form.Group>
            <Button type="submit" className="my-3 w-100">
              SignUp
            </Button>
            <h6 className="text-center bg-danger ">{error.msg}</h6>
          </Form>
        </Card.Body>
      </Card>
      <div className="w-100 text-center mt-2">
        Already have an account? <Link to={"/login"}>Login</Link> here.
      </div>
    </>
  );
}
